import { TodoCounter } from './TodoCounter';
import { TodoSearch } from './TodoSearch';
import { TodoList } from './TodoList';
import { CreateTodoButoon } from './CreateTodoButoon';
import { TodoItem } from './TodoItem';
import React from 'react';

const defaultTodos = [
  { text: 'Cut tomato', completed: true },
  { text: 'Cut apple', completed: false },
  { text: 'Cut lettuce', completed: false },
  { text: 'Cut onion', completed: false },
  { text: 'Cut orange', completed: false },
];

// when the function name starts with a capital letter it is 99% a REACT component
function App() {
  const [todos, setTodos] = React.useState(defaultTodos);
  const [searchValue, setSearchValue] = React.useState('');
  const completedTodos = todos.filter(todo => !!todo.completed).length;
  const totalTodos = todos.length;
  const searchedTodos = todos.filter(
    (todo) => {
      todo.text.includes(searchValue)
    }
  );
  console.log('Los usario bucan todos de ' + searchValue);
  return (
    //we used xml width js
    //labels in lowercase convert them into REACT elements, then transform REACT component
    // <div className="App">
    <React.Fragment>
      <TodoCounter completed={completedTodos} total={totalTodos} />


      <TodoSearch 
        searchValue={searchValue}
        setSearchValue={setSearchValue}
      />

      <TodoList>
        {defaultTodos.map(todo => (
          <TodoItem key={todo.text} 
          text={todo.text}
          completed={todo.completed}
          />
        ))}

        {/* <TodoItem/>
        <TodoItem/>
        <TodoItem/> */}
      </TodoList>
      <CreateTodoButoon/>
      </React.Fragment>
    // </div>
  );
}




export default App;
